mapper.select:
SELECT concat(ci.room_id,"-",ci.id) as cabinetKey, bi.bus_key as busKey, bi.bus_name as busName FROM cabinet_box cb left join cabinet_index ci on cb.cabinet_id = ci.id left join bus_index bi on cb.box_key_a = bi.bus_key
bi.bus_key in
#{item}

SELECT concat(ci.room_id,"-",ci.id) as cabinetKey, bi.bus_key as busKey, bi.bus_name as busName FROM cabinet_box cb left join cabinet_index ci on cb.cabinet_id = ci.id left join bus_index bi on cb.box_key_b = bi.bus_key
bi.bus_key in
#{item}

SELECT cb.id, cb.cabinet_id, cb.box_key_a, cb.outlet_id_a, cb.box_key_b, cb.outlet_id_b, ci.room_id, ci.cabinet_name, ri.room_name FROM cabinet_box cb left join cabinet_index ci on cb.cabinet_id = ci.id left join room_index ri on ci.room_id = ri.id
cb.box_key_a in
#{item}
or cb.box_key_b in
#{item}
