mapper.select:
select ci.id , ci.room_id as roomId, ci.name , ci.aisle_id as aisleId, ci.pow_capacity as powCapacity, ci.pdu_box as pduBox, ci.run_status as runStatus, ci.update_time as updateTime, ci.create_time as createTime, cf.cabinet_height as cabinetHeight, cf.x_coordinate as xCoordinate, cf.y_coordinate as yCoordinate, cf.company from cabinet_index ci left join cabinet_cfg cf on ci.id = cf.cabinet_id where CI.is_disabled = 0 and CI.is_deleted = 0
and ci.pdu_box = #{indexVo.pduBox}
and ci.run_status in
#{item}
and ci.cabinet_id = #{indexVo.cabinet_id}
and ci.roomId = #{indexVo.roomId}
and CF.company like "%"#{indexVo.company}"%"
