mapper.select:
select ci.id , ci.room_id as roomId, ci.cabinet_name as name, ci.aisle_id as aisleId, ci.power_capacity as powCapacity, ci.pdu_box as pduBox, ci.run_status as runStatus, ci.load_status as loadStatus, ci.update_time as updateTime, ci.create_time as createTime, ci.cabinet_height as cabinetHeight, ci.cabinet_height as freeSpace, cf.x_coordinate as xCoordinate, cf.y_coordinate as yCoordinate, cf.company, round((cabinet_use_height/cabinet_height)*100,2) as usedSpaceRate, ri.room_name as roomName, concat(ri.room_name,'-',ci.cabinet_name) as location from cabinet_index ci left join cabinet_cfg cf on ci.id = cf.cabinet_id left join room_index ri on ci.room_id = ri.id where ci.is_disabled = 0 and ci.is_deleted = 0
and ci.pdu_box = #{indexVo.pduBox}
and ci.run_status in
#{item}
and ci.load_status in
#{item}
and ci.id in
#{item}
and ci.roomId = #{indexVo.roomId}
and cf.company like "%"#{indexVo.company}"%"
and round(100-(cabinet_use_height/cabinet_height)*100,2) BETWEEN #{indexVo.startNum} and #{indexVo.endNum}
order by ci.create_time desc,ci.run_status asc

mapper.update:
update cabinet_cfg cg set cg.x_coordinate = #{vo.xCoordinate},cg.y_coordinate =
#{vo.yCoordinate},cg.ele_alarm_day = #{vo.eleAlarmDay}, cg.ele_limit_day = #{vo.eleLimitDay}, cg.ele_alarm_month
= #{vo.eleAlarmMonth}, cg.ele_limit_month = #{vo.eleLimitMonth}
where cg.cabinet_id = #{vo.id}
